sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

// Construct a GlideRecord quesry to find all NeedIt Tasks with Due date
//fields value in the past for the same Assigned to. The record cannot be in the 
//Closed state

	var rightNow = new GlideDateTime();

	var overDueTasks = new GlideRecord('x_58872_needit_needit_task');
	overDueTasks.addQuery('due_date', '<=', rightNow);
	overDueTasks.addQuery('state', '!=', 3);
	overDueTasks.addQuery('assigned_to',current.assigned_to);

	overDueTasks.query();

//Print to the Message field on a Notification
var enoughOverDueTasks = overDueTasks.getRowCount() - 1;

	if (enoughOverDueTasks > 1) {
		template.print("In addition to this overdue NeedIt task, you have " + enoughOverDueTasks + " more overdue NeedIt Tasks.");
	}
	else {
		template.print("You have no any additional tasks.");
	}


})(current, template, email, email_action, event);
