sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

//Query the databse for NeedIt Task records with due date field values older 
//then the current time. Only return NeedIt Task records that do npt have 
//a State field value closed complete

var overDueTask = new GlideRecord('x_58872_needit_needit_task');
overDueTask.addQuery('due_date','<=',rightNow);
overDueTask.addQuery('state','!=',3);

overDueTask.query();

//write log message for each overdue NeedIt task record

while(overDueTask.next()) {
	gs.info("Overdue Needit task record = " + overDueTask.number);
	gs.eventQueue('x_58872_needit.overdueNeedItTask', overDueTask, overDueTask.number, gs.getUserName());
}
