sysauto_script.script:
//Get today's time and date
var rightNow = new GlideDateTime();

//query the database to look for requests with due date within the nearest 24 hours
var dueDateIsToday = new GlideRecord('x_58872_needit_needit_task');
dueDateIsToday.addQuery('due_date','>=', rightNow);
dueDateIsToday.addQuery('due_date','<', gs.hoursAgo(-24));
dueDateIsToday.addQuery('due_date','!=', 3);

dueDateIsToday.query();

//For each ticket write the log with ticket's number

while(dueDateIsToday.next()) {
	gs.info('The ticket with due date within the nearest 24 hours is ' + dueDateIsToday.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask', dueDateIsToday, dueDateIsToday.number, gs.getUserName());
}
